'use strict';let cards=document.querySelectorAll('[aria-label\x3d"Callout box"]');function liveSearch(){let a=document.getElementById("searchbox").value,c=0;for(var b=0;b<cards.length;b++)cards[b].innerText.toLowerCase().includes(a.toLowerCase())?(cards[b].classList.remove("hide"),c+=1):""==a||null==a||0==a.trim().length?cards[b].classList.remove("hide"):cards[b].classList.add("hide"),""!=a&&null!=a&&0!=a.trim().length?document.getElementById("resultsmsg").innerHTML=c+" result(s) found.":document.getElementById("resultsmsg").innerHTML="Result\x26#0040;s\x26#0041; with matched keywords will be displayed in real-time."}let typingTimer,typeInterval=500,searchInput=document.getElementById("searchbox");searchInput&&searchInput.addEventListener("keyup",()=>{clearTimeout(typingTimer);typingTimer=setTimeout(liveSearch,typeInterval)});