'use strict';const x=new IntersectionObserver((a,c)=>{a.forEach(b=>{if(b.isIntersecting){var d=b.target;const u=d.parentElement.querySelector(".lazy-source");u.srcset=u.getAttribute("data-srcset");d.src=d.getAttribute("data-src");c.unobserve(b.target)}})});document.querySelectorAll(".lazy-img").forEach(a=>{x.observe(a)});